create view patient_view as select p.patient_id, p.social_security_number, p.gender, p.phone_number, p.name as patient_name, d.doctor_id, d.name as doctor_name, d.Department_department_id, ms.department_name from doctor d, Medical_Speciality ms, patient p where p.Doctor_doctor_id=d.doctor_id and d.Department_department_id=ms.department_id order by patient_id;

create view patient_reservation_view as select r.reservation_number, r.reservation_date_time, r.Patient_patient_id, p.name as patient_name, r.Medical_Speciality_department_id, ms.department_name from reservation r, patient p, Medical_Speciality ms where p.patient_id=r.Patient_patient_id and ms.department_id=r.Medical_Speciality_department_id;

create view patient_examination_treatment_view as select p.patient_id, p.name as patient_name, d.doctor_id, d.name as doctor_name, e.examination_date_time, e.examination_details, n.nurse_id, n.name as nurse_name, t.treatment_date_time, t.treatment_details from patient p left join examination e on e.Patient_patient_id = p.patient_id left join treatment t on t.Patient_patient_id = p.patient_id left join doctor d on e.Doctor_doctor_id = d.doctor_id left join nurse n on t.Nurse_nurse_id = n.nurse_id where (n.nurse_id is not null or d.doctor_id is not null);